import Picture from './picture';

/** Base class which allows to cluster pictures */
export default class Cluster {
    /**
     * Constructor
     * @param {Array.<Picture>} pictures
     * @param {String} label
     */
    constructor(pictures, label) {
        /**
         * pictures pertaining to this cluster
         * @type {Array.<Picture>}
         */
        this.pictures = pictures;

        /**
         * a label characterizing this cluster
         * @type {string|number}
         */
        this.label = label;
    }

    /**
     * Checks whether a picture is inside this cluster
     * @param {Picture} picture
     * @returns {Boolean} true if picture inside cluster, false else
     */
    hasPicture(picture) {
        for (var keyP in this.pictures) {
            var filename = this.pictures[keyP].getProperties().filename;
            if (filename === picture.filename)
                return true;
        }
        return false;
    }
}

/**
 * @fileOverview Interface for cluster computation
 * @name clusteringstrategy.js
 * @author Florent Grelard
 * @license
 */

/** Interface (abstract class) for cluster computation
 */
export default class ClusteringStrategy {

    /**
     * Deleted constructor
     * @throws {TypeError} if called from abstract class
     */
    constructor() {
        if (this.constructor === ClusteringStrategy) {
            throw new TypeError("Can not construct abstract class.");
        }
        if (this.computeClusters === ClusteringStrategy.prototype.computeClusters) {
            throw new TypeError("Please implement abstract method.");
        }
    }

    /**
     * Compute clusters
     * @throws {TypeError} if called from abstract class
     */
    computeClusters() {
        throw new TypeError("Do not call abstract method computeClusters from child.");
    }
};

/**
 * @fileOverview Dendrogram based on distance from several pictures
 * @name dendrogramstrategy.js
 * @author Florent Grlard
 * @license
 */
import {euclideanDistance} from '../distance';
import Cluster from '../cluster';
import ClusteringStrategy from './clusteringstrategy';


/** Distance strategy to cluster pictures
 */
export default class DistanceStrategy extends ClusteringStrategy {


    /**
     * Overrides constructor
     * @param {Array<Picture>} pictures
     */
    constructor(pictures) {
        super();
        /**
         * pictures
         * @type {Array<Picture>}
         */
        this.pictures = pictures;
    }


    /**
     * Dendrogram strategy
     * @returns {Array<Cluster>} array of clusters
     */
    computeClusters() {
        var indexes = [];
        var clusters = [];
        for (var i = 0; i < this.pictures.length; i++) {
            var pic1 = this.pictures[i];
            var pos1 = pic1.getProperties().position;
            var [minImage, minDistance, index] = this.getClosestImage(pos1);
            var pics = [];
            // if (indexes.indexOf(i) === -1) {
            //     indexes.push(i);
            //     pics.push(pic1);
            // }
            // if (indexes.indexOf(index) === -1) {
            //     indexes.push(index);
            //     pics.push(minImage);
            // }
            pics.push(pic1);
            pics.push(minImage);
            var cluster = new Cluster(pics, minDistance);
            clusters.push(cluster);
        }
        clusters.sort(function(a,b) {
            return a.label < b.label;
        });
        return clusters;
    }

    /**
     * Closest image from a given image
     * @param {Array<number>} pos1
     * @returns {Array<Object>} closest images, and distance
     */
    getClosestImage(pos1) {
        var minDistance = Number.MAX_VALUE;
        var minImage;
        var index = 0;
        for (var j = 0; j < this.pictures.length; j++) {
            var image2 = this.pictures[j];
            var pos2 = image2.getProperties().position;
            var d12 = euclideanDistance(pos1, pos2);
            if (d12 > 0 && d12 < minDistance) {
                minDistance = d12;
                minImage = image2;
                index = j;
            }
        }
        return [minImage, minDistance, index];
    }
}

/**
 * @fileOverview Distance Strategy
 * @name distancestrategy.js
 * @author Florent Grlard
 * @license
 */
import {euclideanDistance} from '../distance';
import Cluster from '../cluster';
import ClusteringStrategy from './clusteringstrategy';

/** Distance strategy based on distance
 */
export default class DistanceStrategy extends ClusteringStrategy {


    /**
     * Overrides constructor
     * @param {Array<Picture>} pictures
     */
    constructor(pictures) {
        super();
        /**
         * pictures
         * @type {Array<Picture>}
         */
        this.pictures = pictures;
    }

    /**
     * Distance strategy: pictures contained in the arc of a givben picture
     * @returns {Array<Cluster>} array of clusters
     */
    computeClusters() {
        var indexes = [];
        var clusters = [];
        for (var i = 0; i < this.pictures.length; i++) {
            if (indexes.indexOf(i) !== -1) continue;
            var pic1 = this.pictures[i];
            var pos1 = pic1.getProperties().position;
            var r1 = pic1.getProperties().arc.radius;
            var cluster = [];
            for (var j = i+1; j < this.pictures.length; j++) {
                if (indexes.indexOf(j) !== -1) continue;
                var pic2 = this.pictures[j];
                var pos2 = pic2.getProperties().position;
                if (euclideanDistance(pos1, pos2) < r1) {
                    indexes.push(j);
                    cluster.push(pic2);
                }
            }
            cluster.push(pic1);
            clusters.push(new Cluster(cluster, pic1.getProperties().filename));
        }
        return clusters;
    }
}

/**
 * Computes the euclidean distance between two points
 * @param {Array} point1
 * @param {Array} point2
 * @returns {Number} the euclidean distance between point 1 and point 2
 */
export function euclideanDistance(point1, point2) {
    var sumDiffSq = 0;
    for (var i = 0; i < point1.length; i++) {
        sumDiffSq += Math.pow((point1[i] - point2[i]), 2);
    }
    return Math.sqrt(sumDiffSq);
}

/**
 * @fileOverview Affine dual
 * @name dual.js
 * @author Florent Grlard
 * @license
 */
import {angleToVector, boundingBox, bboxArrayToObject} from './geometry.js';

/** Class representing the affine dual transform
 */
export default class Dual {

    /**
     * Primal line to dual line
     * @param {Array<number>} vector coefficients
     * @param {Array<number>} center position
     * @param {Array<number>} g barycenter
     * @param {boolean=} vertical whether the line is vertical
     * @returns {Array<number>} the dual of the line, a point
     */
    static dualLine(vector, center, g, vertical = false) {
        var centerNorm = [center[0] - g[0],
                          center[1] - g[1]];
        var x =  (vertical) ? vector[0] / vector[1] : vector[1] / vector[0];
        var y = (vertical) ? centerNorm[0] - x * centerNorm[1] : centerNorm[1] - x * centerNorm[0];
        return [x, -y];
    }

    /**
     * Primal cone to dual cone
     * @param {Arc} arc
     * @param {Array<number>} g barycenter
     * @param {boolean=} vertical whether the line is vertical
     * @returns {Array<Array<number>>}  dual of a cone, a pair of coordinates
     */
    static dualCone(arc, g, vertical = false) {
        var dual = [];
        var firstVector = angleToVector(arc.alpha);
        var secondVector = angleToVector(arc.omega);
        var firstLine = this.dualLine(firstVector, arc.center, g, vertical);
        var secondLine = this.dualLine(secondVector, arc.center, g, vertical);

        var positions = [firstLine, secondLine];
        return positions;
    }

    /**
     * bounding dual rectangle of coordinates
     * @param {Arc} arc
     * @param {Array<number>} g barycenter
     * @param {boolean=} vertical whether the sector contains the vertical line
     * @returns {Array} the bounding box
     */
    static dualBoundingRectangle(arc, g, vertical = false) {
        var dualArc = this.dualCone(arc, g, vertical);
        var bbox = boundingBox(dualArc);
        var bboxCoordinates = bboxArrayToObject(bbox, arc);
        return [bboxCoordinates];
    }

    /**
     * Intersection between primal point and rectangle
     * @param {Array<number>} point request point
     * @param {Array} rectangle the bounding box
     * @returns {boolean} whether it intersects or not
     */
    static intersectionRequestRectangle(point, rectangle) {
        var a = point[0];
        var b = -point[1];
        var low = [rectangle.minX, rectangle.minY];
        var up = [rectangle.maxX, rectangle.maxY];

        var lowI = [(low[1] - b) / a, a * low[0] + b];
        var upI = [(up[1] - b) / a, a * up[0] + b];

        var condition = ((lowI[0] >= low[0] && lowI[0] <= up[0]) ||
                         (upI[0] >= low[0] && upI[0] <= up[0]) ||
                         (lowI[1] >= low[1] && lowI[1] <= up[1]) ||
                         (upI[1] >= low[1] && upI[1] <= up[1]));
        return condition;
    }
}

/**
 * @fileOverview Dual R-tree, using the R-bush library
 * @name dualrtree.js
 * @author Florent Grlard
 * @license
 */
import {centerOfMass} from './geometry.js';
import {euclideanDistance} from './distance.js';
import rbush from 'rbush';

/** Dual R-tree constructed from different types of dualities
 * {@link PolarDual} and {@link Dual}
 */
export default class DualRtree {
    /**
     * Constructor
     * @param {Duality} duality type of duality
     * @param {number} branchingFactor
     * @param {boolean=} divide creates two data structures if true (for vertical lines)
     */
    constructor(duality, branchingFactor, divide = false) {
        /**
         * Whether to create two data structures or not
         * @type {boolean}
         */
        this.divide = divide;

        /**
         * Type of duality : polar or affine
         * @type {Duality}
         */
        this.Duality = duality;

        /**
         * Origin (barycenter)
         * @type {Array<number>}
         */
        this.origin = [];

        /**
         * Data structure
         * @type {rbush}
         */
        this.rtree = rbush(branchingFactor);
        if (this.divide) {
            /**
             * Data structure for vertical lines
             * @type {rbush}
             */
            this.rtreeVertical = rbush(branchingFactor);
        }
    }


    /**
     * Dual representation of angular sectors
     * @param {Arc} arcs
     * @returns {Array<Object>} dual representation
     */
    dualRepresentation(arcs) {
        var dual = [];
        var dualVertical = [];
        for (var arc of arcs) {
            var bboxCoordinates = this.Duality.dualBoundingRectangle(arc, this.origin, false);
            if (this.divide) {
                var bboxCoordinatesVertical = this.Duality.dualBoundingRectangle(arc, this.origin, true);
                var bboxH = bboxCoordinates[0];
                var bboxV = bboxCoordinates[1];
                var rectangleH = [ [bboxH.minX, bboxH.minY],
                                   [bboxH.maxX, bboxH.maxY] ];
                var rectangleV = [ [bboxV.minX, bboxV.minY],
                                   [bboxV.maxX, bboxV.maxY] ];

                var dH = euclideanDistance(rectangleH[0], rectangleH[1]);
                var dV = euclideanDistance(rectangleV[0], rectangleV[1]);

                if (dH <= dV) {
                    dual.push(bboxCoordinates);
                }
                else {
                    dualVertical.push(bboxCoordinatesVertical);
                }
            }
            else {
                for (let rect of bboxCoordinates) {
                    dual.push(rect);
                }
            }


        }
        return [dual, dualVertical];
    }

    /**
     * Inserts angular sectors into the data structure as their duals
     * @param {Arc} arcs
     * @returns {rbush} constructed dual R-tree
     */
    load(arcs) {
        this.origin = centerOfMass(arcs.map(function(arc) {
            return arc.center;
        }));
        var dual = this.dualRepresentation(arcs);
        this.rtree.load(dual[0]);
        if (this.divide) {
            var dualY = dual[1];
            this.rtreeVertical.load(dualY);
        }
    }

    /**
     * Search recursive in the dual R-tree
     * @param {Array} hits
     * @param {Node} node
     * @param {Array} p current point
     * @param {Object} number number of hits
     */
    searchRecursive(hits, node, p, number = {cpt: 0}) {
        number.cpt++;
        if (node.leaf) {
            hits.push(...node.children);
            return;
        }
        for (var child of node.children) {
            var rectangle = {minX: child.minX,
                             minY: child.minY,
                             maxX: child.maxX,
                             maxY: child.maxY};
            if (this.Duality.intersectionRequestRectangle(p, rectangle)) {
                this.searchRecursive(hits, child, p, number);
            }
        }
        return;
    }

    /**
     * Search function
     * @param {Array<number>} p point to search for
     * @returns {Object} hits and number of hits
     */
    search(p) {
        var hits = [];
        var request = [p[0] - this.origin[0],
                       p[1] - this.origin[1]];
        var number = {cpt : 0};
        this.searchRecursive(hits, this.rtree.data, request, number);
        if (this.divide) {
            request = [request[1], request[0]];
            this.searchRecursive(hits, this.rtreeVertical.data, request, number);
        }
        return {hits: hits, number: number};
    }

    /**
     * Getter for the data structure
     * @returns {Array} the data structure
     */
    dataStructure() {
        return this.rtree.data;
    }
}

import transpose from 'transpose';
import Arc from './arc';

/**
 * Converts lon lat to decimal values
 * @param {number} deg
 * @param {number} min
 * @param {number} sec
 * @returns {number} decimal
 */
export function lonLatToDecimal(deg, min, sec) {
    return deg + min / 60 + sec / 3600;
}


/**
 * Converts raw exif to json
 * @param {string} metadata
 * @returns {json} converted json
 */
export function convertMetadataToJSON(metadata) {
    const transposed = transpose(metadata.data);
    const headers = transposed.shift();
    const res = transposed.map(function(row) {
        return row.reduce(function(acc, col, ind) {
            acc[headers[ind]] = col;
            return acc;
        }, {});
    })[0];
    return res;
}

/**
 * Array of coordinates from a given string
 * @param {string} positionString
 * @returns {Array<number>} coordinates
 */
export function createPositionArray(positionString) {
    var patt1 = /[0-9.]/g;
    var patt2 = /[a-zA-Z]/g;
    var arrayPos = [];
    var numberString = "";
    for (var i = 0; i < positionString.length; i++) {
        var character = positionString.charAt(i);
        if (character.match(patt1)) {
            numberString += character;
        }
        else {
            if (numberString != '')
                arrayPos.push(Number(numberString));
            numberString = '';
        }
    }
    return arrayPos;
}

/**
 * Get position from metadata
 * @param {json} mapMetadata metadata
 * @returns {Array<number>}  position
 */
export function getPosition(mapMetadata) {
    if (mapMetadata.hasOwnProperty('GPSPosition')) {
        var positionString = mapMetadata.GPSPosition;
        var positionSplit = positionString.split(",");
        if (positionSplit.length >= 2) {
            var firstPos = positionSplit[0];
            var secondPos = positionSplit[1];
            var fPosDMS = createPositionArray(firstPos);
            var sPosDMS = createPositionArray(secondPos);
            var fPosDec = lonLatToDecimal(fPosDMS[0], fPosDMS[1], fPosDMS[2]);
            var sPosDec = lonLatToDecimal(sPosDMS[0], sPosDMS[1], sPosDMS[2]);
            var lonLat = [sPosDec, fPosDec];
            return lonLat;
        }
    }
    return null;
}

/**
 * Computes the starting and ending angle of the visibility cone (=angular sector), corresponding to the field of view and the direction of the picture
 * @param {number} direction angle in degrees
 * @param {number} fov angle of aperture in degrees
 * @returns {Array<number>} angles of the visibility cone
 */
export function computeAlphaOmegaFromDir(direction, fov) {
    var dirTrigRad = (360 - direction + 90) % 360;
    var alpha = (dirTrigRad - fov / 2) % 360;
    if (alpha < 0) {
        alpha += 360;
    }
    var omega = (alpha + fov);
    return [alpha, omega];
}

/**
 * Orientation of a picture
 * @param {json} mapMetadata
 * @param {Array} position
 * @returns {Arc} angular sector corresponding to the visibility cone
 */
export function getOrientation(mapMetadata, position) {
    if (mapMetadata.hasOwnProperty('GPSImgDirection') &&
        mapMetadata.hasOwnProperty('Orientation') &&
        mapMetadata.hasOwnProperty('FOV')) {
        var dir = mapMetadata.GPSImgDirection;
        var orientation = mapMetadata.Orientation;
        var fov = Number(mapMetadata.FOV.match(/[0-9.]+/g));
        var angles = computeAlphaOmegaFromDir(dir, fov);
        var radius = 300;
        var arc = new Arc([position[0], position[1]], radius, angles[0], angles[1]);
        arc.computeGeometry();
        return arc;
    }
    return null;
}

/**
 * @fileOverview Various geometry functions useful
 * @name geometry.js
 * @author Florent Grlard
 * @license
 */
import * as THREE from 'three';

/**
 * Converts an angle in degrees to a 2D vector
 * @param {number} angle
 * @returns {Array} the 2D vector
 */
export function  angleToVector(angle) {
    var rad = angle * Math.PI / 180;
    var x = Math.cos(rad);
    var y = Math.sin(rad);
    return [x, y];
}

/**
 * Converts a 2D vector to an angle in degrees
 * @param {Array} vector
 * @param {Array} vectorRef axis
 * @returns {number} angle in radians
 */
export function vectorToAngle(vector, vectorRef) {
    var dot = vectorRef[0]*vector[0] + vectorRef[1]*vector[1];
    var det = vectorRef[0]*vector[1] - vectorRef[1]*vector[0];
    var angle = Math.atan2(det, dot);
    return angle;
}


/**
 * Bounding box of positions
 * @param {Array} positions
 * @returns {Array} bounding box
 */
export function boundingBox(positions) {
    var low = [Number.MAX_VALUE, Number.MAX_VALUE];
    var up = [-Number.MAX_VALUE, -Number.MAX_VALUE];
    for (var i = 0; i < positions.length; i++) {
        var position = positions[i];
        for (let j = 0; j < 2; j++) {
            low[j] = (position[j] < low[j]) ? position[j] : low[j];
            up[j] = (position[j] > up[j]) ? position[j] : up[j];
        }
    }
    return [low, up];
}

/**
 * Bounding box as an object
 * @param {Array} array
 * @param {Object} feature a reference to the original object, useful to keep track of it in a R-tree for instance
 * @returns {Object} the bounding box
 */
export function bboxArrayToObject(array, feature) {
    return {minX: array[0][0],
            minY: array[0][1],
            maxX: array[1][0],
            maxY: array[1][1],
            feature: feature
           };

}

/**
 * Barycenter
 * @param {Array} positions
 * @returns {Array} barycenter
 */
export function centerOfMass(positions) {
    var g = [0,0];
    for (var p of positions) {
        g[0] += p[0];
        g[1] += p[1];
    }
    g[0] /= positions.length;
    g[1] /= positions.length;
    return g;
}


/**
 * Orthogonal projection of a point onto a segment [a,b]
 * @param {Array} p
 * @param {Array} a
 * @param {Array} b
 * @returns {Array} the projection
 */
export function project( p, a, b ) {
    var x1=a[0], y1=a[1], x2=b[0], y2=b[1], x3=p[0], y3=p[1];
    var px = x2-x1, py = y2-y1, dAB = px*px + py*py;
    var u = ((x3 - x1) * px + (y3 - y1) * py) / dAB;
    var x = x1 + u * px, y = y1 + u * py;
    return [x, y];
}


/**
 * Converts spherical coordinates to cartesian
 * @param {Object} spherical
 * @returns {Array} cartesian coordinates
 */
export function sphericalToCartesian(spherical) {
    var phi = spherical.phi;
    var theta = spherical.theta;
    return [
        spherical.norm * Math.sin(phi) * Math.cos(theta),
        spherical.norm * Math.sin(phi) * Math.sin(theta),
        spherical.norm * Math.cos(phi)
    ];

}


/**
 * Converts cartesian coordinates to spherical
 * @param {Array} coord
 * @returns {Object} sphericals
 */
export function cartesianToSpherical( coord ) {
    var x = coord[0];
    var y = coord[1];
    var z = coord[2];
    var radius = Math.sqrt( x * x + y * y + z * z );
    var theta = 0;
    var phi = 0;
	if ( radius !== 0 ) {
        theta = Math.atan2( y, x );
        var clampedRatio = Math.min(Math.max((z/radius), -1), 1);
		phi = Math.acos( clampedRatio );
    }
	return {theta: theta, phi: phi, norm: radius};
}


/**
 * Computes the plane given 3 points
 * @param {Array} p1
 * @param {Array} p2
 * @param {Array} p3
 * @returns {THREE.Plane}
 */
export function planeFromThreePoints(p1, p2, p3) {
    var plane = new THREE.Plane();
    plane.setFromCoplanarPoints(new THREE.Vector3(p1[0], p1[1], p1[2]),
                                new THREE.Vector3(p2[0], p2[1], p2[2]),
                                new THREE.Vector3(p3[0], p3[1], p3[2]));
    return plane;
}

/**
 * Intersection
 * @param {Array} point
 * @param {THREE.Plane} plane
 * @returns {THREE.Vector3|Boolean} the intersection
 */
export function intersectionLinePlane(point, plane) {
    var v1 = new THREE.Vector3(point[0], point[1], point[2]);
    v1 = v1.normalize();
    var line = new THREE.Line3(new THREE.Vector3(0,0,0), v1.multiplyScalar(10000));
    var target = new THREE.Vector3();
    var intersects = plane.intersectLine(line, target);
    if (intersects)
        return target;
    return intersects;
}

/**
 * @fileOverview Code to handle grid of pictures, based on Muuri
 * @name grid.js
 * @author Florent Grlard
 * @license
 */

import $ from 'jquery';
import Muuri from 'muuri';

/**
 * Filters the grid according to values on HTML elements
 * @param {Muuri} grid
 * @returns {boolean} match
 */
export function filter(grid) {
    var filterFieldValue = $('.filter-field').val();
    grid.filter(function (item) {
        var element = item.getElement();
        var isFilterMatch = !filterFieldValue ? true : (element.getAttribute('label') || filterFieldValue) === filterFieldValue;
        return isFilterMatch;
    });
}

/**
 * Changes the number of columns inside the grid according to valyes on HTML elements
 * @param {Muuri} grid
 */
export function changeLayout(grid) {
    var layoutFieldValue = $('.layout-field').val();
    var elements = grid.getItems();
    $.each(elements, function(i, item) {
        item.getElement().className = "item" + layoutFieldValue + " muuri-item";
    });
    grid.refreshItems().layout();
}

/**
 * Creates an empty grid
 * @returns {Muuri}
 */
export function generateGrid() {
    return new Muuri('.grid', {
        items: '.item',
        layout: {
            fillGaps: true
        },
        dragEnabled: true,
        dragStartPredicate: function(item, event) {
            var elemWidth = $(item.getElement()).width();
            var elemHeight = $(item.getElement()).height();
            if (event.srcEvent.layerX < 10 ||
                event.srcEvent.layerY < 10 ||
                event.srcEvent.layerX > elemWidth ||
                event.srcEvent.layerY > elemHeight)
                return false;
            return Muuri.ItemDrag.defaultStartPredicate(item, event);
        }
    });
}

/**
 * Adds an image to the grid
 * @param {Muuri} grid
 * @param {URL} url
 * @param {Array} images
 * @param {string} label
 * @param {number} count
 * @param {number} length
 */
export function loadImageAndFillGrid(grid, url, images, label, count, length) {
    var i = new Image();
    i.addEventListener('dragstart', function (e) {
        e.preventDefault();
    }, false);
    i.onload = function(event) {
        fillGrid(grid, i, images, label, count, length);
    };
    i.src = url;

}

/**
 * Fill a grid of images (callback function)
 * @param {Muuri} grid
 * @param {Image} image
 * @param {Array} images
 * @param {string} label
 * @param {number} count
 * @param {number} length
 */
export function fillGrid(grid, image, images, label, count, length) {
    var divItem = $("<div/>", {
        class: "item" + ($('.layout-field').val() || ""),
        "label": label.label,
        "distance": label.distance
    });
    var divItemContent = $("<div/>", {
        class:"item-content"
    });

    divItemContent.append(image);
    divItem.append(divItemContent);
    images.push(divItem.get(0));
    count.number++;
    if (count.number >= length) {
        grid.remove(grid.getItems(), {removeElements: true});
        grid.add(images, {layout:true});
        grid.refreshItems().layout();
        document.body.className = 'images-loaded';
    }
}

/**
 * @fileOverview Heatmap for polygon intersection
 * @name heatmap.js
 * @author Florent Grlard
 * @license
 */
import PluggableMap from 'ol/PluggableMap.js';
import {defaults as defaultControls} from 'ol/control/util.js';
import {defaults as defaultInteractions} from 'ol/interaction.js';
import {assign} from 'ol/obj.js';
import CanvasImageLayerRenderer from 'ol/renderer/canvas/ImageLayer.js';
import CanvasMapRenderer from 'ol/renderer/canvas/Map.js';
import CanvasTileLayerRenderer from 'ol/renderer/canvas/TileLayer.js';
import CanvasVectorLayerRenderer from 'ol/renderer/canvas/VectorLayer.js';
import CanvasVectorTileLayerRenderer from 'ol/renderer/canvas/VectorTileLayer.js';
import CanvasVectorLayerColormapRenderer from './vectorlayercolormaprenderer.js';

/**
 Heatmap for polygon intersection
 */
export default class HeatMap extends PluggableMap {
    /**
     * Constructor
     * @param {Object} options
     */
    constructor(options) {
        options = assign({}, options);
        if (!options.controls) {
            options.controls = defaultControls();
        }
        if (!options.interactions) {
            options.interactions = defaultInteractions();
        }
        super(options);
    }


    /**
     * Create renderer: registers custom CanvasVectorLayerColormapRenderer for isovist intersection
     * @returns {ol.CanvasMapRenderer}
     */
    createRenderer() {
        var renderer = new CanvasMapRenderer(this);
        renderer.registerLayerRenderers([
            CanvasImageLayerRenderer,
            CanvasTileLayerRenderer,
            CanvasVectorLayerRenderer,
            CanvasVectorTileLayerRenderer,
            CanvasVectorLayerColormapRenderer
        ]);
        return renderer;
    }
}

/**
 * @fileOverview Isovist computation inspired from Suleiman et al, A New Algorithm for 3D Isovists.
 * Space is delimited by a set of segments, and visibility can be determined by circular sectors associated with these segments
 * @name isovistsectors2d.js
 * @author Florent Grelard
 * @license
 */

import LineString from 'ol/geom/LineString';
import Polygon from 'ol/geom/Polygon';
import * as Intersection from './lineintersection';
import {euclideanDistance} from './distance';
import Arc from './arc';

/** Object containing segment and associated visibility sector
 */
class AngleToSegment {
    /**
     * Constructor
     * @param {Arc} angle
     * @param {ol.geom.LineString} segment
     */
    constructor(angle, segment) {
        /**
         * angle
         * @type {Arc}
         */
        this.angle = angle;

        /**
         * segment
         * @type {ol.geom.LineString}
         */
        this.segment = segment;
    }
}

/** Class allowing to compute the isovist in 2D, inspired by Suleiman et al's 'A New Algorithm for 3D Isovists"
 * the space is delimited by a set of segments and visibility can be determined by circular sectors associated with these segments
 */
export default class IsoVist {
    /**
     * Constructor
     * @param {Arc} arc the field of view
     * @param {Array.<ol.Feature>} segments segments in map
     * @param {Boolean} isDisplayPartial if true display only partial visible segments, else display full visible segments
     * @param {Boolean} isDisplayPolygon should display polygon or line segments
     * @param {number} epsilon tolerance for intersection
     */
    constructor(arc, segments, isDisplayPartial = true, isDisplayPolygon = true, epsilon = 0.0001) {
        /**
         * arc the field of view
         * @type {Arc}
         */
        this.arc = arc;

        /**
         * building segments
         * @type {Array.<ol.Feature>} segments segments in map
         */
        this.segments = segments;

        /**
         * if true display only partial visible segments, else display full visible segments
         * @type {Boolean}
         */
        this.isDisplayPartial = isDisplayPartial;

        /**
         * if true, displays polygon, else, union of line segments
         * @type {Boolean}
         */
        this.isDisplayPolygon = isDisplayPolygon;

        /**
         * @type {number} epsilon tolerance for intersection
         */
         this.epsilon = epsilon;
    }

    /**
     * Checks whether a segment is contained inside an arc
     * @param {ol.geom.LineString} segment
     * @returns {boolean} whether segment is inside
     */
    isInsideArc(segment) {
        for (let i = 0; i < 1; i+=0.1) {
            if (this.arc.geometry.intersectsCoordinate(segment.getCoordinateAt(i))) {
                return true;
            }
        }
        return false;
    }

    /**
     * Extracts all segments constituting a polygon
     * @param {ol.geom.Polygon} polygon
     * @param {Arc} arc
     * @returns {Array} segments
     */
    segmentsFromPolygon(polygon, arc) {
        var segments = [];
        var polygonVertices = polygon.getCoordinates()[0];
        for (let i = 0; i < polygonVertices.length-1; i++) {
            var p1 = polygonVertices[i];
            var p2 = polygonVertices[i+1];
            var segment = new LineString([p1, p2]);
            segments.push(segment);
        }
        return segments;
    }

    /**
     * From all the segments in the building, extract only those visible in field of view
     * @returns {Array.<ol.geom.LineString>} array of visible segments
     */
    segmentsIntersectingFOV() {
        var segments = [];
        var geometryArc = this.arc.geometry;
        var extentArc = geometryArc.getExtent();
        var that = this;
        for (let f of this.segments) {
            var geometryFeature = f.getGeometry();
            if (euclideanDistance(geometryFeature.getFirstCoordinate(), that.arc.center) > 2 * that.arc.radius) continue;
            if (geometryArc.intersectsExtent(geometryFeature.getExtent()) &&
                geometryFeature.intersectsExtent(extentArc)) {
                if (geometryFeature.getType() === "Polygon") {
                    var segmentsPolygon = that.segmentsFromPolygon(geometryFeature, geometryArc);
                    Array.prototype.push.apply(segments, segmentsPolygon);
                }
            }
        }
        return segments;
    }



    /**
     * Checks whether a segment is blocking, that is to say it is fully visible from the point of view
     * @param {ol.geom.LineString} segment
     * @param {Array.<ol.geom.LineString>} segments all the segments
     * @returns {Boolean} whether segment is blocking with respect to segments
     */
    isNonBlocking(segment, segments) {
        var position = this.arc.center;
        var p1 = segment.getFirstCoordinate();
        var p2 = segment.getLastCoordinate();

        var s1 = new LineString([position, p1]);
        var s2 = new LineString([position, p2]);

        var toPush = false;
        var that = this;
        for (let s of segments) {
            if (Intersection.segmentsEqual(s, segment))
                continue;
            var i1 = Intersection.segmentsIntersect(s1, s);
            var i2 = Intersection.segmentsIntersect(s2, s);
            if (i1 || i2) {
                if (i1) {
                    if (euclideanDistance([i1.x, i1.y], p1) > that.epsilon &&
                        euclideanDistance([i1.x, i1.y], p2) > that.epsilon)
                        toPush = true;
                }
                if (i2) {
                    if (euclideanDistance([i2.x , i2.y], p1) > that.epsilon &&
                        euclideanDistance([i2.x , i2.y], p2) > that.epsilon)
                        toPush = true;
                }
            }
        }
        return toPush;
    }

    /**
     * Converts a point to the angle that it forms with the x-axis in degrees (theta in spherical coordinates)
     * @param {Array} point
     * @returns {number} angle in degree
     */
    angleFromCoordinates(point) {
        var position = this.arc.center;
        var v = [point[0] - position[0],
                 point[1] - position[1]];

        var dot = v[0];

        var norm = Math.sqrt(Math.pow(v[0], 2) + Math.pow(v[1], 2));

        var angle = Math.atan2(v[1], v[0]) * 360 / (2*Math.PI);
        if (angle < 0)
            angle += 360;
        return angle;
    }

    /**
     * Computes the angle that is blocked by a segment
     * @param {ol.geom.LineString} segment
     * @returns {Arc} the arc corresponding to segment
     */
    visionBlockingArc(segment) {
        var radius = this.arc.radius;
        var position = this.arc.center;
        var p1 = segment.getFirstCoordinate();
        var p2 = segment.getLastCoordinate();

        var alpha = this.angleFromCoordinates(p1);
        var omega = this.angleFromCoordinates(p2);

        if (omega < alpha) {
            var tmp = alpha;
            alpha = omega;
            omega = tmp;
        }
        var diffOA = omega - alpha;
        var diffOA360 = 360+alpha-omega;
        if (diffOA360 < diffOA) {
            var tmp = alpha;
            alpha = omega-360;
            omega = tmp;
        }
        if (omega < this.arc.alpha) {
            alpha += 360;
            omega += 360;
        }

        return new Arc(position, radius, +alpha.toFixed(4), +omega.toFixed(4));
    }

    /**
     * Merges consecutive arcs which have overlapping angles
     * @param {Array.<Arc>} array
     * @returns {Array.<Arc>} simplified array
     */
    mergeOverlappingAngles(array) {
        if (array.length === 1) return array;
        var that = this;
        var trimmedArray = [];
        array.sort(function(a,b) {
            if (a.alpha === b.alpha)
                return a.omega - b.omega;
            return a.alpha - b.alpha;
        });
        var alpha = 0;
        var omega = 0;

        for (let i = 0; i < array.length - 1; i++) {
            var current = array[i];
            var next = array[i+1];

            if (i === 0) {
                alpha = current.alpha;
            }
            if (current.omega > omega) {
                omega = current.omega;
            }
            if (omega < next.alpha) {
                trimmedArray.push(new Arc(that.arc.center, that.arc.radius, alpha, omega));
                alpha = next.alpha;
            }
            if (i === array.length - 2) {
                trimmedArray.push(new Arc(that.arc.center, that.arc.radius, alpha, next.omega));
            }
        };
        return trimmedArray;
    }

    /**
     * Returns the complementary of blocked angles in the field of view
     * @param {Array.<Arc>} blockedAngles
     * @returns {Array.<Arc>} free angles
     */
    freeAngles(blockedAngles) {
        var freeA = [];
        var start = this.arc.alpha;
        var end = this.arc.omega;
        var that = this;
        var index = 0;
        while (index < blockedAngles.length) {
            var currentBlocked = blockedAngles[index];
            if (start < currentBlocked.alpha && start < end) {
                freeA.push(new Arc(this.arc.center, this.arc.radius, start, currentBlocked.alpha));
            }
            if (index === blockedAngles.length - 1 && currentBlocked.omega > that.arc.alpha && currentBlocked.omega < end) {
                freeA.push(new Arc(this.arc.center, this.arc.radius, currentBlocked.omega, end));
            }
            start = (currentBlocked.omega >= start) ? currentBlocked.omega : start;
            index++;
        }
        return freeA;
    }

    /**
     * For partially visible segments, extract the part that is visible from the point of view
     * @param {Array} intersection start point of visible segment
     * @param {ol.geom.LineString} segment full segment
     * @param {Arc} arc
     * @returns {ol.geom.LineString} visible segment
     */
    segmentPartVisible(intersection, segment, arc) {
        var segStart = segment.getFirstCoordinate();
        var segEnd = segment.getLastCoordinate();

        var norm = euclideanDistance(segStart, segEnd);

        var distStart = euclideanDistance(intersection, segStart);
        var distEnd = euclideanDistance(intersection, segEnd);

        var middle = [(intersection.x + (segStart[0] - segEnd[0]) / norm), (intersection.y + (segStart[1] - segEnd[1]) / norm) ];
        var middle2 = [(intersection.x + (segEnd[0] - segStart[0]) / norm), (intersection.y + (segEnd[1] - segStart[1]) / norm) ];
        var p;
        if (arc.geometry.intersectsCoordinate(middle))
            p = segStart;
        else if (arc.geometry.intersectsCoordinate(middle2))
            p = segEnd;
        else {
            p = [intersection.x, intersection.y];
        }
        var visibleSegment = new LineString([[intersection.x, intersection.y], p]);
        return visibleSegment;
    }

    /**
     * Computes partially visible segment(s) from a segment
     * @param {Array.<Arc>} angles free angles
     * @param {ol.geom.LineString} segment
     * @returns {Array.<ol.geom.LineString, Array.<ol.geom.LineString> >} if it is partially visible : first argument = full segment, second argument = array of visible segments ; else null
     */
    partiallyVisibleSegments(angles, segment) {

        var visibleSegment = null;
        var position = this.arc.center;
        var visibleSegments = [];
        var that = this;
        var ps1 = segment.getFirstCoordinate();
        var ps2 = segment.getLastCoordinate();

        for (let angle of angles) {
            if (!angle.geometry)
                angle.computeGeometry();
            var r = that.arc.radius;
            var alphaRad = angle.alpha * Math.PI / 180;
            var omegaRad = angle.omega * Math.PI / 180;
            var p1 = [position[0] + r * Math.cos(alphaRad),
                      position[1] + r * Math.sin(alphaRad)];
            var p2 = [position[0] + r * Math.cos(omegaRad),
                      position[1] + r * Math.sin(omegaRad)];
            var s1 = new LineString([position, p1]);
            var s2 = new LineString([position, p2]);
            var i1 = Intersection.segmentsIntersect(segment, s1);
            var i2 = Intersection.segmentsIntersect(segment, s2);
            if (i1 || i2) {
                if (i1 && i2) {
                    visibleSegment = new LineString([[i1.x, i1.y],
                                                     [i2.x, i2.y]]);
                }
                else if (i1) {
                    visibleSegment = that.segmentPartVisible(i1, segment, angle);
                }
                else if (i2) {
                    visibleSegment = that.segmentPartVisible(i2, segment, angle);
                }
                visibleSegments.push(visibleSegment);
            }
            else {
                var i3 = angle.geometry.intersectsCoordinate(ps1);
                var i4 = angle.geometry.intersectsCoordinate(ps2);
                if (i3 && i4) {
                    visibleSegments.push(segment);
                }
            }
        }

        return (visibleSegments.length > 0) ? [segment, visibleSegments] : null;
    }


    /**
     * Extract all fully visible segments
     * @param {Array.<ol.geom.LineString>} segments all segments from buildings
     * @returns {Array.<ol.geom.LineString>} fully visible segments
     */
    blockingSegments(segments) {
        var blockingSegments = [];
        var that = this;
        for (let segment of segments) {
            var nonBlocking = that.isNonBlocking(segment,
                                                 segments);
            if (!nonBlocking) {
                blockingSegments.push(segment);
            }
        }
        return  blockingSegments;
    }

    /**
     * Extracts all partially visible segments, that is to say to those who are in the free vision field
     * @param {Array.<ol.geom.LineString>} blockingSegments
     * @param {Array.<ol.geom.LineString>} segments
     * @returns {Array.<ol.geom.LineString>} partially visible segments
     */
    freeSegments(blockingSegments, segments) {
        var blockingAngles = [];
        var freeSegments = [];
        var that = this;
        for (let segment of blockingSegments) {
            var blockingAngle = that.visionBlockingArc(segment);
            blockingAngles.push(blockingAngle);
        }
        var trimmedBlockingAngles = this.mergeOverlappingAngles(blockingAngles);
        var freeVisionAngles = this.freeAngles(trimmedBlockingAngles);
        for (let segment of segments) {
            if (blockingSegments.indexOf(segment) === -1) {
                var visibleSegment = that.partiallyVisibleSegments(freeVisionAngles, segment);
                if (visibleSegment && that.isInsideArc(visibleSegment[0]))
                    freeSegments.push(visibleSegment);
            }
        }
        return freeSegments;
    }

    /**
     * Display visible parts of blocking segments
     * @param {Array.<ol.geom.LineString>} blockingSegments
     * @returns {Array.<ol.geom.LineString>} partially visible blocking segments
     */
    visibleBlockingSegments(blockingSegments) {
        var that  = this;
        var visibleSegments = [];
        var position = this.arc.center;

        //Computing blocking segments hidden by other segments
        blockingSegments.sort(function(a,b) {
            return euclideanDistance(a.getClosestPoint(position), position) - euclideanDistance(b.getClosestPoint(position), position);
        });
        var blockingAngles = [];
        var freeSegments = [];
        var freeVisionAngles = [this.arc];
        for (let segment of blockingSegments) {
            var blockingAngle = that.visionBlockingArc(segment);
            var partial = false;
            for (let angle of freeVisionAngles) {
                if ((blockingAngle.alpha < angle.alpha && blockingAngle.omega <= angle.omega && blockingAngle.omega > angle.alpha) ||
                    (blockingAngle.omega > angle.omega && blockingAngle.alpha >= angle.alpha && blockingAngle.alpha < angle.omega)) {
                    var visibleSegment = that.partiallyVisibleSegments(freeVisionAngles, segment);
                    if (visibleSegment)
                        Array.prototype.push.apply(visibleSegments, visibleSegment[1]);
                    partial = true;
                }
            }
            var p1 = segment.getFirstCoordinate();
            var p2 = segment.getLastCoordinate();
            if (!partial && that.isInsideArc(segment))
            {
                Array.prototype.push.apply(visibleSegments, [segment]);
            }

            blockingAngles.push(blockingAngle);
            var trimmedBlockingAngles = that.mergeOverlappingAngles(blockingAngles);
            freeVisionAngles = that.freeAngles(trimmedBlockingAngles);
        }

        return visibleSegments;
    }

    /**
     * Computes the isovist polygon from blocking segments
     * @param {Array.<ol.geom.LineString>} blockingSegments
     * @returns {Array.<ol.geom.Polygon>} the isovist
     */
    visibilityPolygon(blockingSegments) {
        var polygon = [];
        var that = this;
        var anglesToSegments = [];
        var blockingAngles = [];

        for (let segment of blockingSegments) {
            var blockingAngle = that.visionBlockingArc(segment);
            let fc = segment.getFirstCoordinate();
            let lc = segment.getLastCoordinate();
            var angleFC = that.angleFromCoordinates(fc);
            var angleLC = that.angleFromCoordinates(lc);
            angleFC = (angleFC < that.arc.alpha - 1) ? angleFC+360 : angleFC;
            angleLC = (angleLC < that.arc.alpha - 1) ? angleLC+360 : angleLC;
            var first = (angleFC < angleLC) ? fc : lc;
            var last = (angleFC < angleLC) ? lc : fc;
            var orientedSegment = new LineString([first, last]);
            let angleToSegment = new AngleToSegment(blockingAngle, orientedSegment);
            blockingAngles.push(blockingAngle);
            anglesToSegments.push(angleToSegment);
        }
        var trimmedBlockingAngles = this.mergeOverlappingAngles(blockingAngles);
        var freeVisionAngles = this.freeAngles(trimmedBlockingAngles);
        for (let angle of freeVisionAngles) {
            if (angle.omega - angle.alpha < 0.5) continue;
            angle.computeGeometry();
            var freeSegment = new LineString([angle.fullGeometry[1].getFlatCoordinates(),
                                              angle.fullGeometry[2].getFlatCoordinates()]);
            let angleToSegment = new AngleToSegment(angle, freeSegment);
            anglesToSegments.push(angleToSegment);
        }

        anglesToSegments.sort(function(a,b) {
            if (a.angle.alpha === b.angle.alpha)
                return a.angle.omega - b.angle.omega;
            return a.angle.alpha - b.angle.alpha;
        });
        if (anglesToSegments.length > 0) {
            polygon.push(this.arc.center);
            polygon.push(anglesToSegments[0].segment.getFirstCoordinate());
            for (let i = 0; i < anglesToSegments.length; i++) {
                let fc = anglesToSegments[i].segment.getFirstCoordinate();
                let lc = anglesToSegments[i].segment.getLastCoordinate();
                polygon.push(fc);
                polygon.push(lc);
            }
            polygon.push(this.arc.center);
        }
        return new Polygon([polygon]);
    }




    /**
     * Main function
     * @returns {Array.<ol.geom.Polygon>} isovist as the segments from buildings visible from the point of view
     */
    isovist() {
        var visibleSegments = [];
        var segments = this.segmentsIntersectingFOV();
        var position = this.arc.center;
        var blockingSegments = this.blockingSegments(segments);

        if (this.isDisplayPartial) {
            var visibleBlockingSegments = this.visibleBlockingSegments(blockingSegments);
            Array.prototype.push.apply(visibleSegments, visibleBlockingSegments);
        }
        var freeSegments = this.freeSegments(blockingSegments, segments);

        var partiallyVisible = [];
        while (freeSegments.length > 0) {
            freeSegments.sort(function(a,b) {
                return euclideanDistance(position, a[0].getClosestPoint(position)) - euclideanDistance(position, b[0].getClosestPoint(position));
            });
            blockingSegments.push(freeSegments[0][0]);
            partiallyVisible.push(freeSegments[0][1]);
            freeSegments = this.freeSegments(blockingSegments, segments);
        }

        if (this.isDisplayPartial) {
            for (let segments of partiallyVisible) {
                Array.prototype.push.apply(visibleSegments, segments);
            }
        } else {
            Array.prototype.push.apply(visibleSegments, blockingSegments);
        }

        if (this.isDisplayPolygon) {
            var polygon = this.visibilityPolygon(visibleSegments);
            return polygon;
        }
        return visibleSegments;
    }
}

/**
 * @fileOverview Isovist computation inspired from Suleiman et al, A New Algorithm for 3D Isovists.
 * Space is delimited by a set of polygons, and visibility can be determined by spherical coordinates associated with these segments
 * @name isovistsectors3d.js
 * @author Florent Grlard
 * @license
 */

import LineString from 'ol/geom/LineString';
import Polygon from 'ol/geom/Polygon';
import * as Intersection from './lineintersection';
import {euclideanDistance} from './distance';
import {boundingExtent, containsExtent, intersects, getIntersection, getArea} from 'ol/extent';
import Arc from './arc';
import IsoVist2D from './isovistsectors2d.js';
import {toLonLat} from 'ol/proj';
import {cartesianToSpherical, sphericalToCartesian, planeFromThreePoints, intersectionLinePlane} from './geometry';


/** Object containing polygon and associated visibility sector
 */
class PolygonToAngle {
    /**
     * Constructor
     * @param {ol.geom.Polygon} Polygon
     * @param {ol.geom.Polygon} spherical extent
     */
    constructor(polygon, angle) {
        /**
         * polygon
         * @type {ol.geom.Polygon}
         */
        this.polygon = polygon;

        /**
         * angle
         * @type {ol.geom.Polygon}
         */
        this.angle = angle;
    }
}


/** Object containing segment and associated visibility sector
 */
class AngleToSegment {
    /**
     * Constructor
     * @param {Arc} angle
     * @param {ol.geom.LineString} segment
     */
    constructor(angle, segment) {
        /**
         * angle
         * @type {Arc}
         */
        this.angle = angle;

        /**
         * segment
         * @type {ol.geom.LineString}
         */
        this.segment = segment;
    }
}

/** Class allowing to compute the isovist in 3D, inspired by Suleiman et al's 'A New Algorithm for 3D Isovists"
 * the space is delimited by a set of polygons and visibility can be determined by spherical coordiantes associated with these segments
 * WORK IN PROGRESS
 */
export default class IsoVist3D extends IsoVist2D {
    /**
     * Constructor
     * @param {Arc} arc the field of view
     * @param {Array.<ol.Feature>} segments segments in map
     * @param {Boolean} isDisplayPartial if true display only partial visible segments, else display full visible segments
     * @param {Boolean} isDisplayPolygon should display polygon or line segments
     * @param {number} epsilon tolerance for intersection
     */
    constructor(arc, segments, isDisplayPartial = true, isDisplayPolygon = true, epsilon = 0.0001) {
        super(arc, segments, isDisplayPartial, isDisplayPolygon, epsilon);
        /**
         * arc the field of view
         * @type {Arc}
         */
        this.arc = arc;

        /**
         * building segments
         * @type {Array.<ol.Feature>} segments segments in map
         */
        this.segments = segments;

        /**
         * if true display only partial visible segments, else display full visible segments
         * @type {Boolean}
         */
        this.isDisplayPartial = isDisplayPartial;

        /**
         * if true, displays polygon, else, union of line segments
         * @type {Boolean}
         */
        this.isDisplayPolygon = isDisplayPolygon;

        /**
         * @type {number} epsilon tolerance for intersection
         */
        this.epsilon = epsilon;
    }



    /**
     * Converts a segment to a 3D polygon
     * @param {ol.geom.LineString} segment
     * @returns {ol.geom.Polygon} polygon
     */
    segmentTo3DPolygon(segment) {
        var p1 = segment.getFirstCoordinate();
        var p2 = segment.getLastCoordinate();
        var p3 = [p2[0], p2[1], 0];
        var p4 = [p1[0], p1[1], 0];
        return new Polygon([[p1, p2, p3, p4]]);
    }

    /**
     * Converts several segments to polygons
     * @param {Array<ol.geom.LineString>} segments
     * @returns {Array<ol.geom.Polygon>} polygons
     */
    segmentsToPolygons(segments) {
        var polygons = [];
        for (let segment of segments) {
            var p = this.segmentTo3DPolygon(segment);
            polygons.push(p);
        }
        return polygons;
    }

    /**
     * Translation of polygons by reference vector
     * @param {ol.geom.Polygon} polygon
     * @param {Array<number>} reference
     * @returns {ol.geom.Polygon} translated polygons
     */
    translatePolygon(polygon, reference) {
        let coordsTranslated = [];
        for (let coord of polygon.getCoordinates()[0]) {
            var coordTranslated = [coord[0] - reference[0],
                                   coord[1] - reference[1],
                                   coord[2] - reference[2]];
            coordsTranslated.push(coordTranslated);
        }
        return new Polygon([coordsTranslated]);
    }

    /**
     * Converts a polygon in cartesian coordinates to spherical coordinates
     * @param {ol.geom.Polygon} polygon
     * @returns {Object} spherical coordinates
     */
    polygonToSphericalCoordinates(polygon) {
        var sphericals = [];
        // var coordinates = [];
        for (let coord of polygon.getCoordinates()[0]) {
            var coordTranslated = [coord[0] - this.arc.center[0],
                                   coord[1] - this.arc.center[1],
                                   coord[2] - this.arc.center[2]];
            var sphericalNorm = cartesianToSpherical(coordTranslated);

            // sphericalNorm.norm = 1;
            // var c = sphericalToCartesian(sphericalNorm);
            sphericals.push(sphericalNorm);
            // coordinates.push(coordTranslated);
        }
        // var sphericalArray = sphericals.map(spherical => [spherical[0], spherical[1], spherical[2]]);
        // var plane = planeFromThreePoints(coordinates[0], coordinates[1], coordinates[2]);
        // intersectionLinePlane(sphericals[0], plane);
        return sphericals;
    }

    /**
     * Converts spherical coordinates to a polygon
     * @param {Object} sphericals
     * @returns {ol.geom.Polygon} polygon
     */
    sphericalCoordinatesToShape(sphericals) {
        var coords = [];
        for (let s of sphericals) {
            var p = [s.theta, s.phi];
            coords.push(p);
        }
        return new Polygon([coords]);
    }

    /**
     * Converts several polygons to their polygonal shape
     * @param {Array<ol.geom.Polygon>} polygons
     * @returns {Array<Object>} objects
     */
    polygonsToAngle(polygons) {
        var polyAngles = [];
        for (let polygon of polygons) {
            var spherical = this.polygonToSphericalCoordinates(polygon);
            var angleShape = this.sphericalCoordinatesToShape(spherical);
            var pToS = new PolygonToAngle(polygon, angleShape);
            polyAngles.push(pToS);
        }
        return polyAngles;
    }

    /**
     * Minimum distance of each corner in the polygon to the picture's position
     * @param {ol.geom.Polygon} polygon
     * @returns {number} min distance
     */
    polygonToMinDistance(polygon) {
        var distance = Number.MAX_VALUE;
        for (let coord of polygon.getCoordinates()[0]) {
            let norm = euclideanDistance(coord, this.arc.center);
            if (distance > norm) {
                distance = norm;
            }
        }
        return distance;
    }

    /**
     * Checks whether a polygon is blocking, that is to say it is fully visible from the point of view
     * @param {Object} polyAngle polygon to angle
     * @param {Array.<Object>} polyAngles
     * @returns {Boolean} whether polyAngle is blocking with respect to polyAngles
     */
    isNonBlocking(polyAngle, polyAngles) {
        var poly = polyAngle.polygon;
        var angle = polyAngle.angle;
        var extent = angle.getExtent();
        var intersecting = [];
        var isBlocking = true;
        for (let j = 0; j < polyAngles.length; j++) {
            var polyAngle2 = polyAngles[j];
            var poly2 = polyAngle2.polygon;
            var angle2 = polyAngle2.angle;
            var extent2 = angle2.getExtent();

            if (angle.intersectsExtent(extent2) &&
                angle2.intersectsExtent(extent) &&
                getArea(getIntersection(extent, extent2)) > 0) {
                intersecting.push(polyAngle2);
            }
        }
        var minDCurrent = this.polygonToMinDistance(poly);
        for (let e of intersecting) {
            var minDOther = this.polygonToMinDistance(e.polygon);
            if (minDOther < minDCurrent) {
                isBlocking = false;
            }
        }
        return !isBlocking;
    }


    /**
     * Sort according to theta angle from spherical coordinates, then phi
     * @param {Array<ol.geom.Polygon>} angles spherical coordinates
     * @returns {Array<ol.geom.Polygon>} sorted angles
     */
    sortTheta(angles) {
        var tmpAngles = angles.slice();
        tmpAngles.sort(function(a, b) {
            let extentA = a.getExtent();
            let extentB = b.getExtent();
            let thetaMinA = extentA[0];
            let phiMinA = extentA[1];
            let thetaMaxA = extentA[2];
            let phiMaxA = extentA[3];
            let thetaMinB = extentB[0];
            let phiMinB = extentB[1];
            let thetaMaxB = extentB[2];
            let phiMaxB = extentB[3];
            if (phiMinA === phiMinB && thetaMaxA === thetaMaxB && thetaMinA === thetaMinB)
                return phiMaxA - phiMaxB;
            if (thetaMaxA === thetaMaxB && thetaMinA === thetaMinB)
                return phiMinA - phiMinB;
            if (thetaMinA === thetaMinB)
                return thetaMaxA - thetaMaxB;
            return thetaMinA - thetaMinB;
        });
        return tmpAngles;
    }


    /**
     * Minimum and maximum of angle
     * @param {Array<ol.geom.Polygon>} angles
     * @param {boolean=} theta theta or phi
     * @returns {Array} min and max
     */
    minMaxAngle(angles, theta = false) {
        var minX = Number.MAX_VALUE;
        var maxX = -Number.MAX_VALUE;
        for (let angle of angles) {
            var extent = angle.getExtent();
            var angleMin, angleMax;
            if (theta) {
                angleMin = extent[0];
                angleMax = extent[2];
            } else {
                angleMin = extent[1];
                angleMax = extent[3];
            }

            if (angleMin < minX) {
                minX = angleMin;
            }
            if (angleMax > maxX) {
                maxX = angleMax;
            }
        }
        return [minX, maxX];
    }


    /**
     * Computes the extent from a polygon
     * @param {ol.extent.Extent} extent
     * @returns {ol.geom.Polygon} polygon
     */
    polygonFromExtent(extent) {
        var minX = extent[0];
        var minY = extent[1];
        var maxX = extent[2];
        var maxY = extent[3];
        var p1 = [minX, minY];
        var p2 = [minX, maxY];
        var p3 = [maxX, maxY];
        var p4 = [maxX, minY];

        return new Polygon([[ p1, p2, p3, p4 ]]);
    }


    /**
     * Merges consecutive spherical coordinates
     * @param {Array.<Object>} angles spherical coordinates
     * @returns {Array.<Arc>} simplified array
     */
    mergeOverlappingAngles(angles) {
        if (angles.length === 1) return angles;
        var sortedAngles = this.sortTheta(angles);
        let minX =  2 * Math.PI;
        let minY =  2 * Math.PI;
        let maxX = -2 * Math.PI;
        let maxY = -2 * Math.PI;
        var locallyIntersecting = [];
        var trimmedArray = [];
        for (let i= 0; i < sortedAngles.length - 1; i++) {
            let current = sortedAngles[i];
            let next = sortedAngles[i+1];

            let extentCurrent = current.getExtent();
            let extentNext = next.getExtent();
            let thetaMinCurrent = extentCurrent[0];
            let phiMinCurrent = extentCurrent[1];
            let thetaMaxCurrent = extentCurrent[2];
            let phiMaxCurrent = extentCurrent[3];

            let thetaMinNext = extentNext[0];
            let phiMinNext = extentNext[1];
            let thetaMaxNext = extentNext[2];
            let phiMaxNext = extentNext[3];
            if (i === 0) {
                minX = thetaMinCurrent;
                minY = phiMinCurrent;
            }
            if (minY > phiMinCurrent) {
                minY = phiMinCurrent;
            }
            if (thetaMaxCurrent > maxX) {
                maxX = thetaMaxCurrent;
            }
            if (phiMaxCurrent > maxY) {
                maxY = phiMaxCurrent;
            }
            if (maxX < thetaMinNext ||
                maxY < phiMinNext) {
                let newExtent = boundingExtent([[minX, minY], [maxX, maxY]]);
                let newPolygon = this.polygonFromExtent(newExtent);
                trimmedArray.push(newPolygon);
                minX = thetaMinNext;
                minY = phiMinNext;
                locallyIntersecting = [];
            }
            if (i === sortedAngles.length - 2) {
                if (maxX < thetaMaxNext) {
                    maxX = thetaMaxNext;
                }
                if (maxY < phiMaxNext) {
                    maxY = phiMaxNext;
                }
                if (minY > phiMinNext) {
                    minY = phiMinNext;
                }
                let newExtent = boundingExtent([[minX, minY], [maxX, maxY]]);
                let newPolygon = this.polygonFromExtent(newExtent);
                trimmedArray.push(newPolygon);
            }
        }
        return trimmedArray;
    }

    /**
     * Whether a polygon is free, that is to say it is partially visible
     * @param {Object} polyAngle
     * @param {Array<Object>} blockingPolyAngles
     * @returns {boolean} whether it is partially visible
     */
    isFree(polyAngle, blockingPolyAngles) {
        var isFree = true;
        var poly = polyAngle.polygon;
        var angle = polyAngle.angle;
        var extent = angle.getExtent();
        for (let angle2 of blockingPolyAngles) {
            var extent2 = angle2.getExtent();
            if (Intersection.rectangleContains(extent2,extent)) {
                isFree = false;
            }
        }
        return isFree;
    }

    /**
     * Extracts all partially visible polygons, that is to say to those who are in the free vision field
     * @param {Array.<ol.geom.Polygon>} blockingPolyAngles
     * @param {Array.<ol.geom.Polygon>} polyAngles
     * @returns {Array.<ol.geom.Polygon>} partially visible polygons
     */
    freeSegments(blockingPolyAngles, polyAngles) {
        var freeSegments = [];
        var trimmedBlockingAngles = this.mergeOverlappingAngles(blockingPolyAngles.map(bAngle => bAngle.angle));
        for (let polyAngle of polyAngles) {
            var isFree = this.isFree(polyAngle, trimmedBlockingAngles);
            if (blockingPolyAngles.indexOf(polyAngle) === -1 && isFree)
                freeSegments.push(polyAngle);
        }
        return freeSegments;
    }


    /**
     * Projects the spherical intersection in 2D, onto a 3D plane in cartesian coordinates
     * @param {Object} partialPart
     * @returns {ol.geom.Polygon} the 3D polygon in cartesian coordinates
     */
    projectIntersectionOnFace(partialPart) {
        var face = partialPart.polygon;
        var intersection = partialPart.intersection;
        var coordsFace = face.getCoordinates()[0];
        var coordsIntersection = intersection.getCoordinates()[0];
        let cartesianIntersection = [];
        for (let coord of coordsIntersection) {
            let spherical = {theta: coord[0], phi: coord[1], norm: 1};
            let cartesianCoord = sphericalToCartesian(spherical);
            cartesianIntersection.push(cartesianCoord);
        }

        var plane = planeFromThreePoints(coordsFace[0], coordsFace[1], coordsFace[2]);
        var p1 = intersectionLinePlane(cartesianIntersection[0], plane);
        var p2 = intersectionLinePlane(cartesianIntersection[1], plane);
        var p3 = intersectionLinePlane(cartesianIntersection[2], plane);
        var p4 = intersectionLinePlane(cartesianIntersection[3], plane);
        if (!p1 || !p2 || !p3 || !p4)
            return new Polygon([[[0,0,0]]]);
        var polygon = new Polygon([[ Object.values(p1), Object.values(p2), Object.values(p3), Object.values(p4)] ]);
        var translatedPolygon = this.translatePolygon(polygon, [-this.arc.center[0],
                                                                -this.arc.center[1],
                                                                -this.arc.center[2]]);
        return translatedPolygon;
    }

    /**
     * Extracts polygon parts that are partially visible
     * @param {Array<Object>} partialParts
     * @returns {Array<ol.geom.Polygon>} array of polygons
     */
    partiallyVisiblePolygon(partialParts) {
        let arrayPolygons =  [];
        for (let partialPart of partialParts) {
            let projectionPolygon = this.projectIntersectionOnFace(partialPart);
            arrayPolygons.push(projectionPolygon);
        }
        return arrayPolygons;
    }

    /**
     * Polygons that are partially visible
     * @param {Array<Object>} blockingPolyAngles
     * @returns {Array<ol.geom.Polygon>} the parts of polygons
     */
    visibleBlockingSegments(blockingPolyAngles) {
        var that  = this;
        var visibleSegments = [];
        var position = this.arc.center;

        //Computing blocking segments hidden by other segments
        blockingPolyAngles.sort(function(a,b) {
            return that.polygonToMinDistance(a.polygon) - that.polygonToMinDistance(b.polygon);
        });
        var visionField = [];
        for (let polyAngle of blockingPolyAngles) {
            let angleCurrent = polyAngle.angle;
            let extentCurrent = angleCurrent.getExtent();
            let polyCurrent = polyAngle.polygon;
            let isPartial = false;
            let coordinates = this.translatePolygon(polyCurrent, this.arc.center);
            let partialParts = [];
            for (let angleVision of visionField) {
                let extentVision = angleVision.getExtent();
                let intersection = getIntersection(extentCurrent, extentVision);
                // let lowerBound = {theta: intersection[0], phi: intersection[1], norm: 1};
                // let upperBound = {theta: intersection[2], phi: intersection[3], norm: 1};
                // var extentCartesian = [sphericalToCartesian(lowerBound), sphericalToCartesian(upperBound)];
                // extentCartesian = [].concat.apply([], extentCartesian);
                let polyIntersection = this.polygonFromExtent(intersection);
                // console.log(polyIntersection);
                if (angleCurrent.intersectsExtent(extentVision) &&
                    angleVision.intersectsExtent(extentCurrent) &&
                    getArea(intersection) > 0)  {
                    isPartial = true;
                    let objIntersectionToSpherical = {intersection: polyIntersection, polygon: coordinates};
                    this.projectIntersectionOnFace (objIntersectionToSpherical);
                    partialParts.push(objIntersectionToSpherical);
                }
            }
            if (isPartial) {
                var arrayPolygons = this.partiallyVisiblePolygon(partialParts);
                Array.prototype.push.apply(visibleSegments, arrayPolygons);
            } else {
                //visibleSegments.push(polyAngle.polygon);
            }
            //break;
            visionField.push(polyAngle.angle);
            visionField = this.mergeOverlappingAngles(visionField);

        }
        return visibleSegments;
    }

    /**
     * Main function
     * @returns {Array.<ol.geom.Polygon>} isovist as the polygons from buildings visible from the point of view
     */
    isovist() {
        var visibleSegments = [];
        var segments = this.segmentsIntersectingFOV();
        var position = this.arc.center;

        var polygons = this.segmentsToPolygons(segments);
        var polyAngles = this.polygonsToAngle(polygons);
        var blockingSegments = this.blockingSegments(polyAngles);
        //var trimmed = this.mergeOverlappingAngles(toMerge);
        var freeSegments = this.freeSegments(blockingSegments, polyAngles);
        var partiallyVisible = [];
        var that = this;
        while (freeSegments.length > 0) {
            freeSegments.sort(function(a,b) {
                return that.polygonToMinDistance(a.polygon) - that.polygonToMinDistance(b.polygon);
            });
            blockingSegments.push(freeSegments[0]);
            freeSegments = this.freeSegments(blockingSegments, polyAngles);
        }
        var visibleSegments = this.visibleBlockingSegments(blockingSegments);

        var onlyPoly = blockingSegments.map(elem => elem.polygon);
        onlyPoly = visibleSegments;
        //var onlyPoly = trimmed;
        //onlyPoly = blockingPoly.map(elem => elem.polygon);
        //onlyPoly = polyAngles.map(elem => elem.angle);
        console.log(onlyPoly.map(elem => elem.flatCoordinates));
        return onlyPoly;


        var position = this.arc.center;
        var blockingSegments = this.blockingSegments(segments);

        if (this.isDisplayPartial) {
            var visibleBlockingSegments = this.visibleBlockingSegments(blockingSegments);
            Array.prototype.push.apply(visibleSegments, visibleBlockingSegments);
        }
        var freeSegments = this.freeSegments(blockingSegments, segments);

        var partiallyVisible = [];
        while (freeSegments.length > 0) {
            freeSegments.sort(function(a,b) {
                return euclideanDistance(position, a[0].getClosestPoint(position)) - euclideanDistance(position, b[0].getClosestPoint(position));
            });
            blockingSegments.push(freeSegments[0][0]);
            partiallyVisible.push(freeSegments[0][1]);
            freeSegments = this.freeSegments(blockingSegments, segments);
        }

        if (this.isDisplayPartial) {
            for (let segments of partiallyVisible) {
                Array.prototype.push.apply(visibleSegments, segments);
            }
        } else {
            Array.prototype.push.apply(visibleSegments, blockingSegments);
        }
        if (this.isDisplayPolygon) {
            var polygon = this.visibilityPolygon(visibleSegments);

            return polygon;
        }
        return visibleSegments;
    }
}

