/**
 * @fileOverview Class representing a 2D plane
 * @name plane.js
 * @author Florent Grlard
 * @license
 */
import {halfLineIntersection, halfLineAndLineIntersection} from './lineintersection.js';

/**
 * Class representing a 2D plane
 */
export default class Plane {

    /**
     * Constructor
     * @param {Array<number>} center
     * @param {Array<number>} normal
     */
    constructor(center, normal) {
        /**
         * Plane center
         * @type {Array<number>}
         */
        this.center = center;

        /**
         * Plane normal
         * @type {Array<number>}
         */
        this.normal = normal.slice();
    }

    /**
     * Checks whether a point is above the plane
     * @param {Array<number>} p the point
     * @returns {boolean} whether p is above this plane
     */
    isAbove(p) {
        if (this.center[0] === p[0] && this.center[1] === p[1])
            return true;
        var d = this.normal[0] * this.center[0] + this.normal[1] * this.center[1];
	    var valueToCheckForPlane = p[0] * this.normal[0] + p[1] * this.normal[1];
	    return (valueToCheckForPlane >= d);
    }

    /**
     * Checks whether a sector is above the plane (that is to say it does not intersect the plane)
     * @param {Arc} arc the sector
     * @param {boolean=} isComplementary whether the plane is a complementary
     * @param {boolean=} isHalfLine function used for intersection
     * @returns {boolean}  whether the sector is above this plane
     */
    isSectorAbove(arc, isComplementary = false, isHalfLine = false) {
        var func = (isHalfLine) ? halfLineIntersection : halfLineAndLineIntersection;
        var isPointAbove = this.isAbove(arc.center);
        if (isPointAbove) return true;

        if (!arc.fullGeometry) {
            arc.computeGeometry();
        }
        var f  = arc.center;
        var la = arc.fullGeometry[1].getFlatCoordinates();
        var lo = arc.fullGeometry[2].getFlatCoordinates();

        var basisVector = [-this.normal[1], this.normal[0]];
        if (isComplementary) {
            basisVector = [-basisVector[0], -basisVector[1]];
        }
        var fPlane = this.center;
        var lPlane = [this.center[0] + basisVector[0] * 5,
                      this.center[1] + basisVector[1] * 5];

        var i1 = func(f[0], f[1],
                      la[0], la[1],
                      fPlane[0], fPlane[1],
                      lPlane[0], lPlane[1]);
        var i2 = func(f[0], f[1],
                      lo[0], lo[1],
                      fPlane[0], fPlane[1],
                      lPlane[0], lPlane[1]);
        var isSectorAbove = i1 || i2;
        return !!isSectorAbove;
       //return this.isAbove(la) || this.isAbove(lo);
    }

    /**
     * Utility function to display the plane in the console
     * @returns {string} plane as a string
     */
    toString() {
        var cx = Math.round(this.center[0]).toString();
        var cy = Math.round(this.center[1]).toString();
        return "c=(" + cx.slice(0, cy.length) + "," + cy.slice(0, cy.length) +  "), v=(" + this.normal[0].toFixed(2) + "," + this.normal[1].toFixed(2) + ")";
    }

    /**
     * Checks whether two planes are the same
     * @param {Plane} other other plane
     * @returns {boolean} whether this plane and the other plane are the same
     */
    equals(other) {
        return (this.center[0] === other.center[0] &&
                this.center[1] === other.center[1] &&
                this.normal[0] === other.normal[0] &&
                this.normal[1] === other.normal[1]);
    }
}

/**
 * @fileOverview Polar dual
 * @name polardual.js
 * @author Florent Grlard
 * @license
 */

import Dual from './dual.js';
import {project, boundingBox, bboxArrayToObject} from './geometry.js';
import {euclideanDistance} from './distance.js';

/** Class representing the polar dual transform
 */
export default class PolarDual extends Dual {

    /**
     * Primal line to dual line
     * @param {Array<number>} vector coefficients
     * @param {Array<number>} center position
     * @param {Array<number>} g barycenter
     * @param {boolean=} vertical whether the line is vertical
     * @returns {Array<number>} the dual of the line, a point
     */
    static dualLine(vector, center, g, vertical = false) {
        var secondPoint = [center[0] + vector[0]*5,
                           center[1] + vector[1]*5];
        var projection = project(g, center, secondPoint);
        var rho = euclideanDistance(projection, g);
        rho = (projection[1] - g[1] < 0) ? -rho : rho;
        if (center[0] === g[0] && center[1] === g[1]) {
            rho = euclideanDistance(vector, g);
            var theta = Math.acos((vector[0] - g[0]) / rho);
            rho = 0;
        }
        else {
            theta = Math.acos((projection[0] - g[0]) / rho);
        }
        return [theta, rho];
    }

     /**
     * Primal cone to dual cone
     * @param {Arc} arc
     * @param {Array<number>} g barycenter
     * @param {boolean=} vertical whether the line is vertical
     * @returns {Array<Array<number>>}  dual of a cone, a pair of coordinates
     */
    static dualCone(arc, g, vertical = false) {
        var cone = super.dualCone(arc, g, vertical);
        var x0 = cone[0][0];
        var x1 = cone[1][0];
        //For vertical lines
        // if (x0 > x1) {
        //     cone[1][0] = Math.PI + x1;
        // }
        return cone;
    }

    /**
     * bounding dual rectangle of coordinates
     * @param {Arc} arc
     * @param {Array<number>} g barycenter
     * @param {boolean=} vertical whether the sector contains the vertical line
     * @returns {Array} the bounding box
     */
    static dualBoundingRectangle(arc, g, vertical = false) {
        var dualArc = this.dualCone(arc, g, vertical);
        var bbox = boundingBox(dualArc);
        var minX = bbox[0][0];
        var minY = bbox[0][1];
        var maxX = bbox[1][0];
        var maxY = bbox[1][1];

        //Finding local maximum
        var coord = [arc.center[0] - g[0],
                     arc.center[1] - g[1]];
        var max = Math.sqrt(Math.pow(coord[0], 2) +
                            Math.pow(coord[1], 2));
        var theta = Math.atan(coord[1] / coord[0]);
        if (theta < 0) {
            theta += Math.PI;
        }
        if (theta >= minX && theta <= maxX) {
            var rho = coord[0] * Math.cos(theta) + coord[1] * Math.sin(theta);
            if (rho < minY) {
                minY = rho;
            }
            if (rho > maxY) {
                maxY = rho;
            }
        }
        var bboxCoordinates = bboxArrayToObject(boundingBox([[minX, minY], [maxX, maxY]]), arc);

        //For vertical lines : two bounding rectangles
        if (arc.omega%180 > 90 && arc.alpha%180 < 90) {
            var first = dualArc[1];
            var second = dualArc[0];
            var rho1 = coord[0] * Math.cos(0) + coord[1] * Math.sin(0);
            var rho2 = coord[0] * Math.cos(Math.PI) + coord[1] * Math.sin(1);
            var bbox1 = boundingBox([[0, rho1], first]);
            var bbox2 = boundingBox([second, [Math.PI, rho2]]);

            var bboxCoordinates1 = bboxArrayToObject(bbox1, arc);
            var bboxCoordinates2 = bboxArrayToObject(bbox2, arc);
            console.log(arc.omega + " " + arc.alpha);
            return [bboxCoordinates1, bboxCoordinates2];

        }
        else {
            return [bboxCoordinates];
        }
    }

     /**
     * Intersection between primal point and rectangle
     * @param {Array<number>} point request point
     * @param {Array} rectangle the bounding box
     * @returns {boolean} whether it intersects or not
     */
    static intersectionRequestRectangle(point, rectangle) {
        var a = point[0];
        var b = point[1];
        var rho = (x) => a * Math.cos(x) + b * Math.sin(x);
        var theta = (y, shift) => (a < 0) ? Math.acos(-y / R) + shift : Math.acos(y / R) + shift;
        var thetaCounterClockwise = (y, shift) => (a < 0) ? -Math.acos(-y / R) + shift : -Math.acos(y / R) + shift;
        var between = (a, b, c) => (b >= a && b <= c);
        var low = [rectangle.minX, rectangle.minY];
        var up = [rectangle.maxX, rectangle.maxY];

        var R = Math.sqrt(a*a + b*b);
        var alpha = Math.atan(b / a);
        var alphaN = alpha + 2 * Math.PI;

        var lowI = [theta(low[1]), rho(low[0])];
        var upI = [theta(up[1]), rho(up[0])];
        var thetaLPS = theta(low[1], alpha);
        var thetaLNS = theta(low[1], alphaN);
        var thetaLPC = thetaCounterClockwise(low[1], alpha);
        var thetaLNC = thetaCounterClockwise(low[1], alphaN);
        var thetaUPS = theta(up[1], alpha);
        var thetaUNS = theta(up[1], alphaN);
        var thetaUPC = thetaCounterClockwise(up[1], alpha);
        var thetaUNC = thetaCounterClockwise(up[1], alphaN);
        var condition = (between(low[0], thetaLPS, up[0]) ||
                         between(low[0], thetaLNS, up[0]) ||
                         between(low[0], thetaLPC, up[0]) ||
                         between(low[0], thetaLNC, up[0]) ||
                         between(low[0], thetaUPS, up[0]) ||
                         between(low[0], thetaUNS, up[0]) ||
                         between(low[0], thetaUPC, up[0]) ||
                         between(low[0], thetaUNC, up[0]) ||
                         between(low[1], lowI[1], up[1]) ||
                         between(low[1], upI[1], up[1]));
        return condition;
    }
}

import Arc from './arc';

/**
 * Random number between min and max
 * @param {number} min
 * @param {number} max
 * @returns {number} the random number
 */
export function getRandomArbitrary(min, max) {
    return Math.random() * (max - min) + min;
}

/**
 * Random features with position
 * @param {ol.Extent} extent map extent
 * @param {Number} count number of features
 * @returns {Array} array with positions
 */
export function addRandomLocations(extent, count) {
    var coordinates = [];
    for (var i = 0; i < count; i++) {
        var extx = extent[2] - extent[0];
        var exty = extent[3] - extent[1];
        var middlex = extent[0]+extx/2;
        var middley = extent[1]+exty/2;
        var factorx = extx / 3;
        var factory = exty / 3;
        var randomCoordinates = [getRandomArbitrary(middlex-factorx, middlex+factorx), getRandomArbitrary(middley-factory, middley+factory)];
        coordinates.push(randomCoordinates);
    }
    return coordinates;
}


/**
 * Add random arcs (angles) at given locations
 * @param {Array.<Array>} positions the locations
 * @returns {Array.<Arc>} arcs
 */
export function addRandomArcs(positions) {
    var arcs = [];
    for (var i = 0; i < positions.length; i++) {
        var coordinates = positions[i];
        var alpha = getRandomArbitrary(0,360);
        var omega = alpha + getRandomArbitrary(2,7);
        var radius = 100;
        var arc =  new Arc(coordinates, radius, alpha, omega);
        arc.computeGeometry();
        arcs.push(arc);
    }
    return arcs;
}

/**
 * @deprecated
 * @fileOverview Angular sector as two planes delimiting space used by {@link ASTreeSectors}
 * @name sector.js
 * @author Florent Grlard
 * @license
 */

import Plane from './plane';

/**
 * Angular sector as two planes delimiting space used by {@link ASTreeSectors}
 * @deprecated
 */
export default class Sector {

    /**
     * Constructor
     * @param {Array<number>} center center
     * @param {Array<number>} normal1 first plane normal
     * @param {Array<number>} normal2 second plane normal
     */
    constructor(center, normal1, normal2) {
        /**
         * First plane
         * @type {Plane}
         */
        this.firstPlane = new Plane(center, normal1);

        /**
         * Second plane
         * @type {Plane}
         */
        this.secondPlane = new Plane(center, normal2);
    }

    /**
     * Checks whether an angular sector is above this sector
     * @param {Arc} arc
     * @param {boolean=} isLine function to use for intersection
     * @returns {boolean} whether it is above this plane
     */
    isSectorAbove(arc, isLine = false) {
        return (this.firstPlane.isSectorAbove(arc, false, isLine) &&
                this.secondPlane.isSectorAbove(arc, false, isLine));
    }

    /**
     * Checks whether an angular sector is above the complementary of this sector
     * @param {Arc} arc
     * @param {boolean=} isLine function to use for intersection
     * @returns {boolean=} whether it is above this complementary
     */
    isSectorAboveComplementary(arc, isLine = false) {
        return (this.firstPlane.isSectorAbove(arc, false, isLine) ||
                this.secondPlane.isSectorAbove(arc, false, isLine));
    }


    /**
     * Checks whether a point is above this sector
     * @param {Array<number>} p
     * @returns {boolean} whether p is above this sector
     */
    isAbove(p) {
        return (this.firstPlane.isAbove(p) &&
                this.secondPlane.isAbove(p));
    }

    /**
     * Checks whether two sectors are the same
     * @param {Sector} other
     * @returns {boolean} whether the sectors are the same
     */
    equals(other) {
        return (this.firstPlane.equals(other.firstPlane) &&
                this.secondPlane.equals(other.secondPlane));
    }

    /**
     * Checks whether two sectors have the same center
     * @param {Sector} other
     * @returns {boolean} whether they have the same center
     */
    sameCenter(other) {
        return (this.firstPlane.center[0] === other.firstPlane.center[0] &&
                this.firstPlane.center[1] === other.firstPlane.center[1]);
    }

    /**
     * Utility function to display the sector in the console
     * @returns {string} sector as a string
     */
    toString() {
        return this.firstPlane + "\n" + this.secondPlane;
    }
}

/**
 * @fileOverview Allows to poll a Webserver for images
 * isovists and metadata
 * @name serverpoll.js
